import React from "react";

//função arrow do componente Questão01A
const Questao01A = () => {
    const lista = [
        {a: 10, b: 3, c: 7},
        {a: 5, b: -3, c: 9},
        {a: 1, b: 9, c: 40}
    ]

    // passando alista como props e renderizando componente Questão01B
    return (
        <div>
            <h2>Questão 01</h2>
                <Questao01B lista={lista} />
        </div>
    )
}

//função clássica
function Questao01B(props){

    const maiorNum = props.lista.map((num, index) => {
        let max = num.a;

        if(num.b > max){
            max = num.b;
        }
        if(num.c > max){
            max = num.c;
        }


        return (
            <div key={index}>
                <p>Maior valor da lista {index + 1}: {max}</p>
            </div>
        )
    })

    return (
        <div>
            <h2>Maiores números da lista:</h2> 
            {maiorNum}
        </div>
    )
}

export default Questao01A;
